define(['jquery','jquery/ui'],function($){'use strict';$.widget('mage.paymentMethod',{options:{paymentName:'payment[method]',paymentValue:'free'},_create:function(){var options=this.options;$.each(this.element[0].elements,function(){if(this.name===options.paymentName&&this.value===options.paymentValue){$(this).prop({'checked':true,'disabled':false}).parent().show();}else if($(this).closest('div.actions').length===0){$(this).parent().prop('disabled',true);}});}});return $.mage.paymentMethod;});