var Captcha=Class.create();Captcha.prototype={initialize:function(url,formId){this.url=url;this.formId=formId;},refresh:function(elem){window.formId=this.formId;if(elem){Element.addClassName(elem,'refreshing');}
new Ajax.Request(this.url,{onSuccess:function(response){var json;if(response.responseText.isJSON()){json=response.responseText.evalJSON();if(!json.error&&json.imgSrc){$(formId).writeAttribute('src',json.imgSrc);if(elem){Element.removeClassName(elem,'refreshing');}}else{if(elem){Element.removeClassName(elem,'refreshing');}}}},method:'post',parameters:{'formId':this.formId}});}};